package pattern;

public class JavaPattern_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//		  0123
//		0 ****
//		1 ****
//		2 ****
//		3 ****
		for(int i=1;i<5;i++)
		{
			for(int j=1;j<5;j++)
			{
				System.out.print("*");
			}
			System.out.println();
		}

	}

}
