package sample;

public class ElseIfLadder {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Else If Ladder
		int a=5;
		int b=5;
		if(a>b)
		{
			System.out.println("a is greater");
		}
		else if(a<b)
		{
			System.out.println("a is smaller");
		}
		else
		{
			System.out.println("a and b are equal");
		}

	}

}
